	private static LookupTable<Species> speciesTable = new LookupTable<>();
	static {
		for(Species species : Species.values()) {
			speciesTable.put(species.getName(), species);
		}
	}
	public static Species getSpeciesFromString(String name) {
		return speciesTable.get(name);
	}
	
	
