	public Ability getAbilityFromSlot(int ability) {
		if (ability == 1) return this.getAbility1();
		return this.getAbility0();
	}
	
	public int getSlotFromAbility(Ability ability) {
		if(this.getAbility1() == ability)
			return 1;
		return 0;		
	}

